# Part 1
def getFloor(insts):
    up = insts.count("(")
    down = insts.count(")")
    return up - down


# Part 2
def getBasementPosition(insts):
    floor = 0
    for i, x in enumerate(insts):
        if x == "(":
            floor += 1
        else:
            floor += -1

        if floor == -1:
            return i + 1


